INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'0d77d3e5-96d4-e311-8759-000c2935b86f', N'Preventing Vomiting Caused by Cancer Treatment', N'Instructions', N'&lt;font face=&quot;tahoma, arial, verdana, sans-serif&quot;&gt;â€‹&lt;/font&gt;&lt;div class=&quot;block block-delta-blocks  block-page-title block-delta-blocks-page-title odd block-without-title&quot; id=&quot;block-delta-blocks-page-title&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 12px; color: rgb(80, 80, 80);&quot;&gt;&lt;div class=&quot;block-inner clearfix&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;content clearfix&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;h1 id=&quot;page-title&quot; class=&quot;title&quot; style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1.6rem; font-style: inherit; font-variant: inherit; line-height: 1.1; letter-spacing: -0.05em; color: rgb(0, 0, 128);&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Preventing Vomiting Caused by Cancer Treatment&lt;/font&gt;&lt;/h1&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;div class=&quot;block block-system  block-main block-system-main even block-without-title&quot; id=&quot;block-system-main&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; line-height: 12px; color: rgb(80, 80, 80);&quot;&gt;&lt;div class=&quot;block-inner clearfix&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;content clearfix&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;article class=&quot;node node-page node-published node-not-promoted node-not-sticky author-matthewharaszkiewcz odd clearfix&quot; id=&quot;node-page-29891&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;content clearfix&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;&lt;span class=&quot;print-link&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;/span&gt;&lt;/font&gt;&lt;div class=&quot;field field-name-body field-type-text-with-summary field-label-hidden&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;field-items&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;field-item even&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;span style=&quot;font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;To help doctors give their patients the best possible care, the American Society of Clinical Oncology (ASCO) developed evidence-based recommendations on the use of antiemetics (drugs that prevent vomiting). These guidelines were updated in 2011 because the latest research showed better prevention of vomiting with new combinations of medications and some additional medications became available. This guide for patients receiving any type of chemotherapy or radiation therapy is based on these updated ASCO recommendations.&lt;/font&gt;&lt;/span&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Key Messages&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;ul type=&quot;disc&quot; style=&quot;margin: 0px 0px 10px 3em; padding: 0px 0px 0px 1.2em; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; list-style-position: initial; list-style-image: initial;&quot;&gt;&lt;li style=&quot;margin: 19.484375px 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.3;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;The risk of nausea and vomiting depends on the specific chemotherapy and the type and location of radiation therapy you receive.&lt;/font&gt;&lt;/li&gt;&lt;li style=&quot;margin: 19.484375px 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.3;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;With appropriate medications, vomiting can be prevented in most patients undergoing cancer treatment.&lt;/font&gt;&lt;/li&gt;&lt;/ul&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Background&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;div class=&quot;field field-name-body field-type-text-with-summary field-label-hidden&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;field-items&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;div class=&quot;field-item even&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Emesis (vomiting or throwing up) is the act of expelling the contents of the stomach through the mouth. Nausea is the urge to vomit.&amp;nbsp;&lt;a href=&quot;http://www.cancer.net/patient/All+About+Cancer/Cancer.Net+Feature+Articles/Treatments%2C+Tests%2C+and+Procedures/Understanding+Radiation+Therapy&quot; alt=&quot;Radiation therapy&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; color: rgb(0, 0, 128); text-decoration: none;&quot;&gt;Radiation therapy&lt;/a&gt;&amp;nbsp;(the use of high-energy x-rays or other particles to kill cancer cells) and some types of&amp;nbsp;&lt;a href=&quot;http://www.cancer.net/patient/All+About+Cancer/Cancer.Net+Feature+Articles/Treatments%2C+Tests%2C+and+Procedures/Understanding+Chemotherapy&quot; alt=&quot;chemotherapy&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; color: rgb(0, 0, 128); text-decoration: none;&quot;&gt;chemotherapy&lt;/a&gt;&amp;nbsp;(the use of drugs to kill cancer cells) cause&amp;nbsp;&lt;a href=&quot;http://www.cancer.net/patient/All+About+Cancer/Treating+Cancer/Managing+Side+Effects/Nausea+and+Vomiting&quot; alt=&quot;nausea and vomiting&quot; style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; color: rgb(0, 0, 128); text-decoration: none;&quot;&gt;nausea and vomiting&lt;/a&gt;, although not all patients who receive these treatments will have these side effects. Patients who often have motion sickness or have vomited after previous chemotherapy treatment are more likely to experience nausea and vomiting.&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;The best way to manage nausea and vomiting caused by cancer treatment is to prevent it. Fortunately, many medications are available that can prevent vomiting. These medications may also prevent nausea, but some patients may still have nausea even if they do not have vomiting. More research is needed to understand if these drugs prevent nausea.&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Anticipatory emesis is vomiting that occurs before treatment in patients who have previously felt nauseous or vomited after chemotherapy. The prevention and treatment of anticipatory vomiting depends on the patient. Tell your doctor if you have experienced vomiting with previous chemotherapy treatment. Your doctor may be able to recommend medication or behavioral therapy to help reduce vomiting.&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Recommendations for Preventing Vomiting Caused by Chemotherapy&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Some chemotherapy is more likely to cause nausea and vomiting than other chemotherapy. The table (Table 1) below lists the likelihood that a certain intravenous (IV; given in a vein) chemotherapy will cause nausea and vomiting.&lt;/font&gt;&lt;/p&gt;&lt;p style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Table 1. The Risk of Nausea and Vomiting From Intravenous Chemotherapy&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;table border=&quot;1&quot; cellpadding=&quot;0&quot; cellspacing=&quot;0&quot; width=&quot;100%&quot; style=&quot;border-collapse: collapse; margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit; border-spacing: 0px;&quot;&gt;&lt;tbody style=&quot;border: 0px; margin: 0px; padding: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;tr style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;td style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;p align=&quot;center&quot; style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;Nearly Always Causes Nausea and Vomiting&lt;/strong&gt;&lt;br&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;(High Risk)&lt;/strong&gt;&lt;/font&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;p align=&quot;center&quot; style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 1rem; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: 1.5;&quot;&gt;&lt;strong style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: 11px; font-style: inherit; font-variant: inherit; line-height: inherit;&quot;&gt;&lt;font face=&quot;tahoma&quot;&gt;Usually Causes Nausea and Vomiting&lt;br&gt;(Moderate Risk)&lt;/font&gt;&lt;/strong&gt;&lt;/p&gt;&lt;/td&gt;&lt;td style=&quot;margin: 0px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline; font-size: inherit; font-style: inherit; font-variant: inherit; font-weight: inherit; line-height: inherit;&quot;&gt;&lt;p align=&quot;center&quot; style=&quot;margin: 0px 0px 10px; padding: 0px; border: 0px; outline: 0px; vertical-align: baseline