INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'8a0495c6-0bd7-e311-8759-000c2935b86f', N'Minimal Emetic Risk', N'Risks', N'&lt;p class=&quot;MsoNormal&quot;&gt;&lt;b&gt;&lt;u&gt;ASCO Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;No antiemetic administered routinely pre- or postchemotherapy.&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot;&gt;&lt;br&gt;&lt;b&gt;&lt;u&gt;NCCN Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;No routine prophylaxis; consider using antiemetics listed under primary prophylaxis as treatment.&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'e87efa98-88d7-e311-8759-000c2935b86f', N'Low Emetic Risk', N'Risks', N'&lt;b&gt;&lt;u&gt;ASCO Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12pt; line-height: 18.399999618530273px; font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Dexamethasone (8 mg) recommended; no routine preventive use of antiemetics for delayed emesis recommended.&lt;/span&gt;&lt;br&gt;&lt;br&gt;&lt;b&gt;&lt;u&gt;NCCN Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;&lt;span style=&quot;font-size: 12pt; line-height: 18.399999618530273px; font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Metoclopramide, with or without diphenhydramine; dexamethasone (12 mg); or prochlorperazine recommended, with or without lorazepam.&lt;/span&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'885fcfac-0bd7-e311-8759-000c2935b86f', N'Moderate Emetic Risk', N'Risks', N'&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in;&quot;&gt;&lt;b&gt;&lt;u&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;ASCO Guidelines&lt;/span&gt;&lt;/u&gt;&lt;/b&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;&lt;br&gt;For patients receiving an anthracycline and cyclophosphamide, the three-drug combination of a 5-HT&lt;sub&gt;3&lt;/sub&gt;&lt;span class=&quot;apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;receptor antagonist, dexamethasone, and aprepitant recommended prechemotherapy; single-agent aprepitant recommended on days 2 and 3 for prevention of delayed emesis.&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;For patients receiving other chemotherapies of moderate emetic risk, the two-drug combination of a 5-HT3&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;receptor antagonist and dexamethasone recommended prechemotherapy; single-agent dexamethasone or a 5-HT3&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;receptor antagonist recommended on days 2 and 3 for prevention of delayed emesis.&lt;/span&gt;&lt;br&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;&lt;br&gt;&lt;b&gt;&lt;u&gt;NCCN Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;For patients receiving an anthracycline and cyclophosphamide and selected patients receiving other chemotherapies of moderate emetic risk (e.g., carboplatin, cisplatin, doxorubicin, epirubicin, ifosfamide, irinotecan, or methotrexate), a 5-HT&lt;sub&gt;3&lt;/sub&gt;&lt;span class=&quot;apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;receptor antagonist (ondansetron, granisetron, dolasetron, or palonosetron&lt;sup&gt;b&lt;/sup&gt;), dexamethasone (12 mg), and aprepitant (125 mg) recommended, with or without lorazepam, prechemotherapy; for other patients, aprepitant is not recommended.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in;&quot;&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;For prevention of delayed emesis, dexamethasone (8 mg) or a 5-HT&lt;sub&gt;3&lt;/sub&gt;&amp;nbsp;receptor antagonist on days 2â€“4 or, if used on day 1, aprepitant (80 mg) on days 2 and 3, with or without dexamethasone (8 mg) on days 2â€“4, recommended, with or without lorazepam on days 2â€“4.&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'385850a6-88d7-e311-8759-000c2935b86f', N'High Emetic Risk', N'Risks', N'&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in;&quot;&gt;&lt;b&gt;&lt;u&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;ASCO Guidelines&lt;/span&gt;&lt;/u&gt;&lt;/b&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;&lt;br&gt;For patients receiving an anthracycline and cyclophosphamide, the three-drug combination of a 5-HT&lt;sub&gt;3&lt;/sub&gt;&lt;span class=&quot;apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;receptor antagonist, dexamethasone, and aprepitant recommended prechemotherapy; single-agent aprepitant recommended on days 2 and 3 for prevention of delayed emesis.&lt;br&gt;&lt;/span&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;For patients receiving other chemotherapies of moderate emetic risk, the two-drug combination of a 5-HT3&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;receptor antagonist and dexamethasone recommended prechemotherapy; single-agent dexamethasone or a 5-HT3&lt;/span&gt;&amp;nbsp;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;receptor antagonist recommended on days 2 and 3 for prevention of delayed emesis.&lt;/span&gt;&lt;br&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;&lt;br&gt;&lt;b&gt;&lt;u&gt;NCCN Guidelines&lt;/u&gt;&lt;/b&gt;&lt;br&gt;For patients receiving an anthracycline and cyclophosphamide and selected patients receiving other chemotherapies of moderate emetic risk (e.g., carboplatin, cisplatin, doxorubicin, epirubicin, ifosfamide, irinotecan, or methotrexate), a 5-HT&lt;sub&gt;3&lt;/sub&gt;&lt;span class=&quot;apple-converted-space&quot;&gt;&amp;nbsp;&lt;/span&gt;receptor antagonist (ondansetron, granisetron, dolasetron, or palonosetron&lt;sup&gt;b&lt;/sup&gt;), dexamethasone (12 mg), and aprepitant (125 mg) recommended, with or without lorazepam, prechemotherapy; for other patients, aprepitant is not recommended.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in;&quot;&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;For prevention of delayed emesis, dexamethasone (8 mg) or a 5-HT&lt;sub&gt;3&lt;/sub&gt;&amp;nbsp;receptor antagonist on days 2â€“4 or, if used on day 1, aprepitant (80 mg) on days 2 and 3, with or without dexamethasone (8 mg) on days 2â€“4, recommended, with or without lorazepam on days 2â€“4.&lt;/span&gt;&lt;span style=&quot;font-size: 9pt; font-family: Tahoma, sans-serif;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'44ffcbe6-0bd7-e311-8759-000c2935b86f', N'Neutropenia-1', N'Risks', N'&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:3.0pt;margin-right:0in;margin-bottom:3.0pt;
margin-left:0in;line-height:14.4pt;mso-outline-level:1;vertical-align:baseline&quot;&gt;&lt;b&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Antimicrobial
Prophylaxis and Outpatient Management of Fever and Neutropenia in Adults
Treated for Malignancy: American Society of Clinical Oncology Clinical Practice
Guideline&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif; border: 1pt none windowtext; padding: 0in;&quot;&gt;Purpose: To provide guidelines on antimicrobial prophylaxis for
adult neutropenic oncology outpatients and on selection and treatment as
outpatients of those with fever and neutropenia.&lt;/span&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:6.0pt;margin-right:0in;margin-bottom:3.0pt;
margin-left:0in&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Recommendation:&amp;nbsp; Antibacterial and antifungal prophylaxis are
only recommended for patients expected to have 100 neutrophils/L for 7 days,
unless other factors increase risks for complications or mortality to similar
levels. Inpatient treatment is standard to manage febrile neutropenic episodes,
although carefully selected patients may be managed as outpatients after
systematic assessment beginning with a validated risk index (eg, Multinational
Association for Supportive Care in Cancer [MASCC] score or Talcottâ€™s rules). Patients
with MASCC scores 21 or in Talcott group 4, and without other risk factors,can
be managed safely as outpatients. Febrile neutropenic patients should receive
initial doses of empirical antibacterial therapy within an hour of triage and
should either be monitored for at least 4 hours to determine suitability for
outpatient management or be admitted to the hospital. An oral fluoroquinolone
plus amoxicillin/clavulanate (or plus clindamycin if penicillin allergic) is
recommended as empiric therapy, unless fluoroquinolone prophylaxis was used
before fever developed.&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 3pt; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Published in Journal of Clinical Oncology, Vol 31, Issue 3
(February), 2013: 794-810&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'414deaf7-0bd7-e311-8759-000c2935b86f', N'Neutropenia-2', N'Risks', N'&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:3.0pt;margin-right:0in;margin-bottom:3.0pt;
margin-left:0in;line-height:14.4pt;mso-outline-level:1;vertical-align:baseline&quot;&gt;&lt;b&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Antimicrobial
Prophylaxis and Outpatient Management of Fever and Neutropenia in Adults
Treated for Malignancy: American Society of Clinical Oncology Clinical Practice
Guideline&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif; border: 1pt none windowtext; padding: 0in;&quot;&gt;Purpose: To provide guidelines on antimicrobial prophylaxis for
adult neutropenic oncology outpatients and on selection and treatment as
outpatients of those with fever and neutropenia.&lt;/span&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:6.0pt;margin-right:0in;margin-bottom:3.0pt;
margin-left:0in&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Recommendation:&amp;nbsp; Antibacterial and antifungal prophylaxis are
only recommended for patients expected to have 100 neutrophils/L for 7 days,
unless other factors increase risks for complications or mortality to similar
levels. Inpatient treatment is standard to manage febrile neutropenic episodes,
although carefully selected patients may be managed as outpatients after
systematic assessment beginning with a validated risk index (eg, Multinational
Association for Supportive Care in Cancer [MASCC] score or Talcottâ€™s rules). Patients
with MASCC scores 21 or in Talcott group 4, and without other risk factors,can
be managed safely as outpatients. Febrile neutropenic patients should receive
initial doses of empirical antibacterial therapy within an hour of triage and
should either be monitored for at least 4 hours to determine suitability for
outpatient management or be admitted to the hospital. An oral fluoroquinolone
plus amoxicillin/clavulanate (or plus clindamycin if penicillin allergic) is
recommended as empiric therapy, unless fluoroquinolone prophylaxis was used
before fever developed.&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 3pt; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Published in Journal of Clinical Oncology, Vol 31, Issue 3
(February), 2013: 794-810&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'15a33cee-0bd7-e311-8759-000c2935b86f', N'Neutropenia-3', N'Risks', N'&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:3.0pt;margin-right:0in;margin-bottom:3.0pt;margin-left:0in;line-height:14.4pt;mso-outline-level:1;vertical-align:baseline&quot;&gt;&lt;b&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;AntimicrobialProphylaxis and Outpatient Management of Fever and Neutropenia in Adults Treated for Malignancy: American Society of Clinical Oncology Clinical Practice Guideline&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/b&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 3pt 0in; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif; border: 1pt none windowtext; padding: 0in;&quot;&gt;Purpose: To provide guidelines on antimicrobial prophylaxis for adult neutropenic oncology outpatients and on selection and treatment as outpatients of those with fever and neutropenia.&lt;/span&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:6.0pt;margin-right:0in;margin-bottom:3.0pt;margin-left:0in&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Recommendation:&amp;nbsp; Antibacterial and antifungal prophylaxis are only recommended for patients expected to have 100 neutrophils/L for 7 days, unless other factors increase risks for complications or mortality to similar levels. Inpatient treatment is standard to manage febrile neutropenic episodes, although carefully selected patients may be managed as outpatients after systematic assessment beginning with a validated risk index (eg, Multinational Association for Supportive Care in Cancer [MASCC] score or Talcottâ€™s rules). Patients with MASCC scores 21 or in Talcott group 4, and without other risk factors,can be managed safely as outpatients. Febrile neutropenic patients should receive initial doses of empirical antibacterial therapy within an hour of triage and should either be monitored for at least 4 hours to determine suitability for outpatient management or be admitted to the hospital. An oral fluoroquinolone plus amoxicillin/clavulanate (or plus clindamycin if penicillin allergic) is recommended as empiric therapy, unless fluoroquinolone prophylaxis was used before fever developed.&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/p&gt; &lt;p class=&quot;MsoNormal&quot; style=&quot;margin: 6pt 0in 3pt; vertical-align: baseline;&quot;&gt;&lt;span style=&quot;font-family: &#39;Times New Roman&#39;, serif;&quot;&gt;Published in Journal of Clinical Oncology, Vol 31, Issue 3 (February), 2013: 794-810&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'281fb404-88d7-e311-8759-000c2935b86f', N'Constipation', N'Instructions', N'&lt;span style=&quot;font-family: Arial, sans-serif;&quot;&gt;Constipation occurs when a person cannot empty his or her bowels or has a
feeling of needing to move the bowels but can&#39;t. It starts when the body begins
absorbing more water or begins signaling food to move through the bowel system
more slowly. Constipation is a common symptom in people with cancer.&lt;/span&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Relieving side effects, also called symptom management, &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25282&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;palliative care&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[2]&lt;/span&gt;,
or supportive care, is an important part of cancer care and treatment. Several
factors affect a person&#39;s normal bowel function, including how much and what
kind of food he or she is eating, the health of his or her gastrointestinal
(GI) tract, and the medications the person is taking. Talk with your health
care team about any constipation symptoms you experience, including any new
symptoms or a change in symptoms. These can often be controlled by taking some
simple steps, but occasionally these symptoms are a sign of a more serious
problem that may require further testing.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Surgery to the abdomen often affects the function of the GI tract and may
cause either &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25247&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;diarrhea&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[3]&lt;/span&gt; or constipation. Sometimes after abdominal
surgery, scar tissue (called adhesions) can cause narrowing or partial blockage
of the bowel which, when severe, may require surgery to reverse. Also, cancer
deposits may grow in the abdomen, interfering with bowel function. The most
serious form of this condition is a &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25244&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;bowel obstruction&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[4]&lt;/span&gt;, where the bowel becomes blocked by tumor. A
person may require surgery to reverse this problem.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Symptoms&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;In addition to not being able to empty the bowels, people with constipation
may experience the following symptoms:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Pain
     and cramping&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Swelling
     in the abdomen&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Appetite
     loss&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Nausea
     and vomiting&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Inability
     to urinate&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Confusion&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Causes&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Common causes of constipation include not eating enough fiber, not drinking
enough water or other fluids, and not exercising. However, for people with
cancer, the following cancer-related factors and cancer treatments might also
cause constipation:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Medications,
     including antacids; some drugs to treat nausea and vomiting, seizures,
     depression, diarrhea, or blood pressure; iron supplements; chemotherapy;
     and especially pain medications which can slow the muscular action of the
     bowel that helps food move through the system.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eating
     less food&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Bowel
     obstruction from scar tissue or tumors&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Dehydration
     (not drinking enough water)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Being
     physically inactive&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Tumor
     compression (cancer pressing on the spinal cord)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;High
     levels of calcium in the blood&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Low
     potassium levels&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Thyroid
     problems&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Diabetes&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Diagnosis&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;In planning treatment for constipation, the doctor may do a rectal
examination or take an x-ray or other imaging scan of the abdomen. This is to
make sure you don&#39;t have a tumor obstructing your rectum or at another point in
the GI tract and to find out whether there is hard stool in your rectum. The
doctor may also ask you about the following:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Your
     bowel habits, before the cancer and now&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Use
     of medications that may cause constipation&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;What
     you&#39;ve been eating and drinking recently&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Other
     diseases or illnesses&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Treatment&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;It is very important to treat constipation properly. If it is left
untreated, constipation may cause internal damage to the intestine or rectum,
dehydration, or bowel obstruction. It can also slow the body&#39;s absorption of
medications taken by mouth. If there is scar tissue or a tumor causing the
problem, additional tests may be necessary. Talk with your health care team
about the best way to manage constipation. Some of the following suggestions
may help.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Drink
     more liquids.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;With
     the advice of the health care team, ask about changing the dose or
     stopping medications that cause constipation.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eat
     more fiber or take fiber supplements. However, if you have adhesions or
     tumor deposits that narrow your bowel, your doctor may recommend a
     low-fiber diet instead, also called a low-residue diet,&amp;nbsp; because the
     fiber may back up behind the narrower areas of the bowel.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Increase
     physical activity, if possible.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Ask
     your health care team about treatment, such as laxatives, an enema, or
     rectal suppository. Some of these may be harmful for some patients.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:0in;margin-right:0in;margin-bottom:0in;
margin-left:.25in;margin-bottom:.0001pt&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;This section has been reviewed and approved by the &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/about-us&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;Cancer.Net Editorial Board&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[1]&lt;/span&gt;, &lt;span class=&quot;date-display-single&quot;&gt;February /
2014&lt;/span&gt; &lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'38636414-88d7-e311-8759-000c2935b86f', N'Edema', N'Instructions', N'&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Edema is swelling caused by the abnormal buildup of fluid in the body. The
fluid collects under the skin within the tissues that are outside of the
circulatory system (the system that carries blood through the body). Edema is
most common in the feet and legs, but it can occur in the hands, arms, face,
abdomen (where it is called &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25049&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;ascites&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[2]&lt;/span&gt;), and around the lungs (where it is called &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25249&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;pleural effusion&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[3]&lt;/span&gt;).&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Causes&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Edema may be caused by the following factors:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Cancer,
     especially kidney, liver, or ovarian cancers&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Some
     types of chemotherapy, including cisplatin (Platinol) and docetaxel
     (Taxotere)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Other
     medications, including corticosteroids (medications used to reduce
     swelling), hormone replacement medications, nonsteroidal anti-inflammatory
     drugs (such as ibuprofen or naproxen), and some blood pressure medications&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Low
     levels of protein in the blood, caused by poor nutrition&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Inactivity,
     which can cause fluid to collect in the feet and legs&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Problems
     with kidney, liver, or heart function&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Symptoms&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;People with edema may experience the following symptoms:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Puffiness,
     swelling, or a â€œheavyâ€ feeling&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;The
     feeling that clothes, shoes, rings, or watches are too tight&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Decreased
     flexibility of the joints in the arms and legs, such as the ankles,
     wrists, and fingers&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Shiny,
     tight, or stiff skin&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Indentation
     of the skin when it is pressed (Although, the skin no longer indents when
     edema is severe.)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Sudden
     or rapid weight gain&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Decreased
     amount of urine&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Diagnosis&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;To diagnose edema, your doctor may perform a physical examination to check
whether the skin over the swollen area indents when pressed and may ask you
questions about recent weight gain, tightness of clothes or jewelry, and other
symptoms. The doctor may also order blood and urine tests and x-rays.&amp;nbsp;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Management&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&amp;nbsp;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Relieving side effectsâ€”also called symptom management, &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25282&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;palliative care&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[4]&lt;/span&gt;,
or supportive careâ€”is an important part of cancer care and treatment. Talk with
your health care team about any symptoms you experience, including any new
symptoms or a change in symptoms.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Managing edema focuses on correcting the underlying cause of fluid buildup.
Edema caused by medications or poor nutrition is reversible in some people.
Edema caused by cancer or by kidney, heart, or liver problems may be more
difficult to treat and may be permanent. The following suggestions may help
reduce swelling and relieve symptoms:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Ask
     your doctor about prescription diuretics (medications that eliminate
     excess fluid from the body by increasing urination).&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eat
     a well-balanced diet.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Reduce
     the amount of salt in your diet.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Walk
     or do other exercises, which helps pump fluids back to your heart.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Elevate
     the affected area when sitting or lying down.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Avoid
     standing for long periods or sitting with your legs crossed.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Wear
     compression stockings or elastic sleeves to help push fluids back into
     your circulation system.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Do
     not reduce amount of water or other fluids you drink without consulting
     your doctor.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Talk
     with your doctor about whether physical therapy or occupational therapy
     may be helpful.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:0in;margin-right:0in;margin-bottom:0in;
margin-left:.25in;margin-bottom:.0001pt&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;
line-height:115%;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;
mso-ansi-language:EN&quot;&gt;This section has been reviewed and approved by the &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/about-us&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;
line-height:115%;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;color:windowtext;
mso-ansi-language:EN;text-decoration:none;text-underline:none&quot;&gt;Cancer.Net
Editorial Board&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;line-height:
115%;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;/span&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;line-height:
115%;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;mso-ansi-language:EN&quot;&gt;[1]&lt;/span&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;line-height:115%;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;mso-ansi-language:EN&quot;&gt;, &lt;/span&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;line-height:115%;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;
mso-ansi-language:EN&quot;&gt;July / 2012&lt;/span&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:12.0pt;
line-height:115%;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'5803742b-88d7-e311-8759-000c2935b86f', N'Fatigue', N'Instructions', N'&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Cancer-related fatigue is a persistent feeling of physical, emotional, or
mental tiredness or exhaustion related to cancer and/or its treatment. This
type of fatigue is different than other types of fatigue, such as when a
healthy person does not get enough rest, because it interferes with a personâ€™s
usual functioning, does not reflect their level of activity, and does not
improve with rest. Most people receiving cancer treatment experience fatigue,
and some cancer survivors have fatigue that lasts for months and sometimes
years after finishing treatment.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;It is important to talk with your health care team about any symptoms of
fatigue you may experience, including any new symptoms or a change in symptoms.
Diagnosing and relieving side effects, also called symptom management, &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25282&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;palliative care&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[2]&lt;/span&gt;,
or supportive care, is an important part of your cancer care and treatment.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;How fatigue affects your quality of life&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Fatigue often negatively affects the overall physical, psychological,
social, and economic well-being of a person with cancer. For some, it is
slightly bothersome, while for others the experience can be overwhelming.
Fatigue may influence your:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Daily
     activities&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Hobbies
     and other enjoyable activities&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Social
     relationships&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Mood
     and emotions&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Job
     performance&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Feeling
     of well-being and sense of joy&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Attitude
     toward the future&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Ability
     to undergo treatment&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Screening and diagnosing fatigue&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;ASCO recommends that you receive an evaluation of your level of fatigue
when you are first diagnosed with cancer and that your level of fatigue is
assessed throughout treatment and into recovery. In addition, your health care
provider should perform this evaluation each year and any time you develop
symptoms of fatigue. This evaluation is part of a comprehensive screening
called distress screening that evaluates your emotional health and quality of
life.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Not all of the causes of cancer-related fatigue are well understood, and
your fatigue may be the result of more than one cause. To help find the best
way for you to manage your fatigue, your doctor will work with you to look at
several factors:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Fatigue history&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;. As part of your fatigue history, you may be asked to
describe your fatigue on a scale from â€œno fatigueâ€ to â€œmost fatigue.â€ Your
doctor may also ask when the fatigue began, when you feel most tired, how long
it lasts, if it has changed over time, and what makes it better or worse.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Health changes related to cancer.&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; Your doctor may take a blood sample
or perform other tests to learn more about potential cancer-related causes of
your fatigue, such as if it is worsening, has spread, or has come back after
treatment.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Other health conditions.&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; Other factors can cause or worsen fatigue. Your doctor
may ask questions or recommend tests to find out if another health condition is
affecting your level of fatigue.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Treating the causes of fatigue&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;The first step in managing fatigue is to treat any medical cause of fatigue
or any condition that is contributing to fatigue.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/24585&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Managing pain&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[3]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; Living with constant pain will almost
always make a person feel exhausted. Many of the medications prescribed for the
treatment of pain also cause drowsiness, sleepiness, and fatigue. Your doctor
can help you understand the pain management options available and give you
information about common side effects of pain medications.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Managing &lt;/span&gt;&lt;/b&gt;&lt;a href=&quot;http://www.cancer.net/coping-and-emotions/managing-emotions/depression&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;depression&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[4]&lt;/span&gt;&lt;b&gt;, &lt;/b&gt;&lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/coping-and-emotions/managing-emotions/anxiety&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;anxiety&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[5]&lt;/span&gt;&lt;b&gt;, and &lt;/b&gt;&lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/24589&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;stress&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[6]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; Dealing with cancer may lead to
feelings of distress, depression, and anxiety, which often increase feelings of
exhaustion and complicate treatment. Managing stress and treating depression
and anxiety usually makes a huge difference in a patientâ€™s level of fatigue.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/25058&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Getting enough
sleep&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[7]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; Stress,
pain, and worry often interfere with a person&#39;s ability to sleep through the
night. In some cases, medications may also disturb normal sleep patterns. For
those who are chronically tired, sleep may come in spurts at different times of
the day or evening. Not feeling refreshed by sleep or being unable to sleep
more than one to two hours will contribute to feelings of exhaustion and will
likely affect your mood and ability to function.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/24985&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eating healthy&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[8]&lt;/span&gt;&lt;b&gt;. &lt;/b&gt;Eating well and drinking enough fluids
are important to maintaining an adequate weight and meeting your bodyâ€™s
nutritional needs. If available, consider talking with a nutrition counselor or
registered dietitian (RD) at your treatment center. He or she can provide
helpful hints on eating a well-balanced diet, as well as on eating when there
is a lack of taste and during times of nausea and vomiting.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/25926&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Treating anemia&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[9]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; Many patients with cancer have anemia,
which is a decrease in the amount of circulating red blood cells. Anemia may be
caused by the cancer or cancer-related treatments. Patients who have anemia
report a feeling of extreme and overwhelming fatigue. The treatment for anemia
may include nutritional supplements, drugs, and/or blood transfusions.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Managing medication side effects.&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; Medications used to treat cancer
also contribute to fatigue. For example, it is common for fatigue to appear
after treatment at the following times:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;A
     few days after &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/30673&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;chemotherapy&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[10]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;A
     few weeks after beginning &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/30687&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;radiation
     treatment&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
     mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[11]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;After
     treatment with &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/30679&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;immunotherapy&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[12]&lt;/span&gt;, such as interferon alpha (Alferon,
     Intron A, or Roferon-A) and interleukin (Proleukin)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;In addition, medications used to manage other side effects may contribute
to fatigue, such as those used to manage pain (see above).&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/24479&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Co-existing
medical conditions&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[13]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; People
with cancer may also have other health conditions in addition to cancer,
especially if they are older. These conditions can also cause or worsen fatigue
and include heart problems, lowered lung and kidney function, hormone problems,
arthritis, and nerve problems.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Other strategies to cope with fatigue&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Along with treating and managing the medical causes of fatigue, many
patients find that lifestyle changes can help them better cope with fatigue. &lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/24996&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Physical Activity&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[14]&lt;/span&gt;&lt;b&gt;. &lt;/b&gt;Staying physically active or
increasing your activity level can help relieve cancer-related fatigue. Start
slowly and gradually increase your activity levels. Walking programs are generally
considered safe for most survivors. Once you are healthy enough for physical
activity, try to aim for or build up to 150 minutes of moderate activity, such
as fast walking, cycling, or swimming per week plus two to three strength
training sessions per week. Talk with your doctor about the level and types of
physical activity best for you. Many survivors, particularly those who have a
higher risk of injury due to the cancer, its treatment, or other health
conditions, benefit from working with a physical therapist or exercise
specialist who can find the best ways to increase or maintain their physical
functioning.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/30666&quot;&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Therapy and
counseling&lt;/span&gt;&lt;/b&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[15]&lt;/span&gt;&lt;b&gt;.&lt;/b&gt; Talking
with a therapist or counselor specially trained to work with cancer survivors
can help reduce fatigue. Specifically, a type of counseling called cognitive
behavioral therapy or behavioral therapy can help patients reframe their
thoughts about fatigue and improve poor coping skills and/or sleep problems
that could contribute to fatigue.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Mind-body strategies.&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; There is some evidence that mindfulness-based
approaches, yoga, and acupuncture can reduce fatigue in cancer survivors. Touch
therapy, massage, music therapy, relaxation, a form of touch therapy called
reiki, and a type of relaxation/meditation called qigong may also be beneficial
for reducing fatigue, but more research is needed on these strategies. If you
are interested in pursuing mind-body strategies to help manage your fatigue,
talk with your health care team for a referral to professionals who specialize
in using these methods for cancer survivors.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Medications and supplements.&lt;/span&gt;&lt;/b&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; Some medications that make people
feel more alert and awake may help manage fatigue for patients who are
receiving cancer treatment or have advanced cancer. However, there is not much
evidence that they are helpful for cancer survivors, but research on
supplements, such as ginseng and vitamin D, is ongoing. &amp;nbsp;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-bottom:0in;margin-bottom:.0001pt&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;This
section has been reviewed and approved by the &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/about-us&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;Cancer.Net Editorial Board&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[1]&lt;/span&gt;, &lt;span class=&quot;date-display-single&quot;&gt;April / 2014&lt;/span&gt;.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'd8215538-88d7-e311-8759-000c2935b86f', N'Weight Loss', N'Instructions', N'&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Weight loss is common among people with cancer and is often the first
noticeable sign of the disease. As many as 40% of people with cancer report
unexplained weight loss at the time of diagnosis, and up to 80% of people with &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25113&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;advanced cancer&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[2]&lt;/span&gt;
(cancer that cannot be cured) experience weight loss and cachexia (or wasting),
which is the combination of weight loss and muscle mass loss. It is common for
people with solid tumor cancers (such as lung and breast cancers) and people
with blood cancers (such as leukemia and lymphoma) to experience weight loss.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;The condition is associated with &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25048&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;fatigue&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[3]&lt;/span&gt;, weakness, loss of
energy, and an inability to perform everyday tasks. People experiencing
cachexia often cannot manage treatments well and may experience more intense
symptoms.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Causes&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Weight loss often begins when a person experiences &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25043&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;appetite loss&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[4]&lt;/span&gt;
or finds food unappealing. This may result from other side effects of cancer or
cancer treatment that include:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Changes
     to the immune system or metabolism (the process of the body breaking down
     food and turning it into energy)&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25052&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Nausea and vomiting&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[5]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25246&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Constipation&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[6]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25051&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Mouth sores&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[7]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25046&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Difficulty swallowing&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[8]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25060&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Loss of taste&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[9]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25480&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Depression&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[10]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;a href=&quot;http://www.cancer.net/node/25259&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Pain&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[11]&lt;/span&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Treatment&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Relieving side effectsÃ¢Â€â€also called symptom management, &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25282&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;palliative care&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[12]&lt;/span&gt;,
or supportive careÃ¢Â€â€is an important part of cancer care and treatment. Talk
with your health care team about any symptoms you experience, including any new
symptoms or a change in symptoms.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Controlling cancer-related weight loss is important for the comfort and
well-being of a person with cancer. The following suggestions may help:&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Increase
     the amount of food you eat by about 450 calories per day.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Consider
     asking your doctor about receiving food through a tube that goes directly
     to the stomach, which may help people with head and neck or esophageal
     cancers who are having &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25045&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;difficulty
     chewing&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
     mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[13]&lt;/span&gt; or &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25046&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
     &amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;difficulty swallowing&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[8]&lt;/span&gt;.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eat
     light meals and avoid protein-rich foods before cancer treatment to help
     prevent aversions (feelings of dislike) to these foods.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Nutrients given through an intravenous (IV) tube (a tube inserted into a
vein) are usually not recommended, except when a person is expected to recover
and requires short-term nutritional support.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;In some cases, doctors may recommend medications to address weight loss.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Megestrol
     acetate (Megace) is a progesterone hormone that can improve appetite,
     weight gain, and a person&#39;s sense of well-being.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Steroid
     medications can increase appetite, improve a person&#39;s sense of well-being,
     and help with nausea, weakness, or pain. Because of serious side effects,
     do not use steroids for more than a few weeks.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Metoclopramide
     (Reglan) can prevent early satiety (feeling full before eating enough
     food).&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Pancreatic
     enzyme (lipase) replacement helps the body absorb fat.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;

&lt;p class=&quot;MsoNormal&quot; style=&quot;margin-top:0in;margin-right:0in;margin-bottom:0in;
margin-left:.25in;margin-bottom:.0001pt&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;This section has been reviewed and approved by the &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/about-us&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;Cancer.Net Editorial Board&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[1]&lt;/span&gt;, &lt;span class=&quot;date-display-single&quot;&gt;February /
2012.&lt;/span&gt; &lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;')
INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'b868f147-88d7-e311-8759-000c2935b86f', N'Dysgeusia', N'Instructions', N'&lt;p&gt;Dysgeusia &lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;(a change in the sense of taste) may occur during or after cancer
treatment. Some foods may taste different than they did in the past, some foods
may taste bland, or every food may taste the same. Specifically, bitter, sweet,
and salty foods may taste different, and some people may have a metallic or
chemical taste in their mouth, especially after eating meat or other
high-protein foods. Taste changes can lead to food aversions (dislikes), &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25043&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;loss of appetite&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[2]&lt;/span&gt;, and &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25263&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;weight loss&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[3]&lt;/span&gt;.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Relieving side effects, also called symptom management, &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25282&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;palliative care&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[4]&lt;/span&gt;,
or supportive care, is an important part of cancer care and treatment. Talk
with your health care team about any changes in taste you may experience,
including any new symptoms or a change in symptoms.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Causes&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Taste changes are a common &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/24676&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;side effect
of chemotherapy&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[5]&lt;/span&gt;; about half of
people receiving chemotherapy experience taste changes. Types of chemotherapy
commonly associated with taste changes include cisplatin (Platinol),
cyclophosphamide (Cytoxan, Neosar), doxorubicin (Adriamycin), fluorouracil
(5-FU, Adrucil), paclitaxel (Taxol), and vincristine (Oncovin, Vincasar PFS).
Taste changes caused by chemotherapy usually stop about three to four weeks
after the end of treatment. Some other medications, including some opioid
medications (pain medications such as morphine that act on the central nervous
system) and antibiotics, can also cause taste changes.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;a href=&quot;http://www.cancer.net/node/24677&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Radiation therapy&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[6]&lt;/span&gt; to the neck and head often cause taste changes
because of damage to the taste buds and salivary glands. It may also cause
changes to the sense of smell. Because smell and taste are closely linked,
changes to the sense of smell may affect how foods taste. Taste changes caused
by radiation treatment usually begin to improve three weeks to two months after
the end of treatment. Improvement may continue for about a year, but the sense
of taste may not entirely return to the way it was before treatment, especially
if there is damage to the salivary glands.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Other causes of taste changes include surgery to the nose, throat, or
mouth; biological therapies, such as interleukin-2 (IL-2, called aldesleukin
[Proleukin]); &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25047&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;dry mouth&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[7]&lt;/span&gt;; damage to the nerves involved in tasting;
mouth infections; dental or gum problems; and &lt;/span&gt;&lt;a href=&quot;http://www.cancer.net/node/25052&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;
mso-ansi-language:EN&quot;&gt;nausea and vomiting&lt;/span&gt;&lt;/a&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt; &lt;span class=&quot;print-footnote&quot;&gt;[8]&lt;/span&gt;.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Management and treatment&lt;/span&gt;&lt;/strong&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:
&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;In most cases, there are no specific treatments for taste problems. Talk
about any changes in taste with your doctor. Treatment from a dentist will help
improve taste changes caused by mouth infections, dry mouth, or dental or gum
problems.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;p&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:
EN&quot;&gt;Consider the following tips to cope with taste changes. Depending on the
cause of taste changes, different tips may work better for some people than for
others.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/p&gt;

&lt;ul type=&quot;disc&quot;&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Choose
     foods that smell and taste good, even if the food is unfamiliar.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eliminate
     cooking smells by using an exhaust fan, cooking on an outdoor grill, or
     buying precooked foods. Cold or room-temperature foods also smell less.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Eat
     cold or frozen food, which may taste better than hot foods, unless you are
     on a chemotherapy called oxaliplatin (Eloxatin]), which makes it difficult
     to ingest anything cold.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Use
     plastic utensils and glass cookware to lessen a metallic taste.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Try
     sugar-free, mint gum or hard candies (with flavors such as mint, lemon, or
     orange) to mask a bitter or metallic taste in the mouth.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Try
     other protein sources (such as poultry, eggs, fish, peanut butter, beans,
     or dairy products) if red meats don&#39;t taste good.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Marinate
     meats in fruit juices, sweet wines, salad dressings, or other sauces.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Flavor
     foods with herbs, spices, sugar, lemon, or sauces.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Do
     not eat one to two hours before and up to three hours after chemotherapy
     to prevent food aversions caused by nausea and vomiting. In addition,
     avoiding favorite foods before chemotherapy helps prevent aversions to
     those foods.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Rinse
     with a salt and baking soda solution (Â½ teaspoon of salt and Â½ teaspoon of
     baking soda in 1 cup of warm water) before meals, which may help neutralize
     bad tastes in the mouth.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Keep
     a clean and healthy mouth by brushing frequently and flossing daily.&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
 &lt;li class=&quot;MsoNormal&quot;&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-family:&amp;quot;Arial&amp;quot;,&amp;quot;sans-serif&amp;quot;;mso-ansi-language:EN&quot;&gt;Consider
     zinc sulfate supplements, which may help improve taste in some people.
     However, consult with your doctor before taking any dietary supplements,
     especially during active treatment.&lt;/span&gt;&lt;span lang=&quot;EN&quot; style=&quot;font-size:
     12.0pt;font-family:&amp;quot;Times New Roman&amp;quot;,&amp;quot;serif&amp;quot;;mso-fareast-font-family:&amp;quot;Times New Roman&amp;quot;;
     mso-ansi-language:EN&quot;&gt;&lt;o:p&gt;&lt;/o:p&gt;&lt;/span&gt;&lt;/li&gt;
&lt;/ul&gt;')
