INSERT [dbo].[SiteCommonInformation] ([ID], [Label], [DataType], [Details]) VALUES (N'c551c60d-c0d6-e311-8759-000c2935b86f', N'Drowsiness', N'Instructions', N'&lt;div class=&quot;tgroup2col&quot; style=&quot;width: 647px; float: left; color: rgb(51, 51, 51); font-family: &#39;Lucida Grande&#39;, Geneva, Arial, Helvetica, sans-serif; font-size: 13px; line-height: 19.68000030517578px;&quot;&gt;&lt;div class=&quot;titlediv&quot; style=&quot;width: 452.890625px; float: left;&quot;&gt;&lt;h1 class=&quot;pheader&quot; style=&quot;margin: 0px 0px 0.5em; font-size: 1.6em; line-height: 1.2; color: rgb(0, 130, 200); float: left;&quot;&gt;Drowsiness&lt;/h1&gt;&lt;/div&gt;&lt;div class=&quot;sharebuttons&quot; style=&quot;width: 194.09375px; float: right;&quot;&gt;&lt;div id=&quot;printemail&quot; class=&quot;printemail&quot; style=&quot;margin-top: 4px; float: right;&quot;&gt;&lt;div class=&quot;addthis_toolbox addthis_default_style &quot; id=&quot;mp_s2&quot; style=&quot;float: right; margin-right: -10px;&quot;&gt;&lt;a title=&quot;Email this page to a friend&quot; href=&quot;http://www.nlm.nih.gov/cgi/medlineplus/email_request.pl?refPage=http://www.nlm.nih.gov/medlineplus/ency/article/003208.htm&amp;amp;emailTitle=Drowsiness&quot; target=&quot;EmailWin&quot; class=&quot;mp_email&quot; style=&quot;outline: none; color: rgb(153, 0, 102); margin-right: 10px; float: left; display: block;&quot;&gt;&lt;span class=&quot;_mpemail&quot; style=&quot;background-image: url(http://www.nlm.nih.gov/medlineplus/images/email_sm.png); background-attachment: scroll; background-color: transparent; display: block; height: 16px; overflow: hidden; width: 16px; float: left; padding: 0px 2px; line-height: 16px !important; cursor: pointer; background-position: 0px 0px; background-repeat: no-repeat no-repeat;&quot;&gt;&lt;/span&gt;&lt;span style=&quot;left: -9999px; position: absolute;&quot;&gt;Email this page to a friend&lt;/span&gt;&lt;/a&gt;&lt;a class=&quot;addthis_button_facebook at300b&quot; tit